import dummy from "../Data/Dummy.json"

function FirstPage() 
{ 
  dummy.PostTable.Title = dummy.PostTable.Day = dummy.PostTable.Post = "";

  return (
    <div className="Appa">  

      <div>
      제목 ::  <input onChange={(e)=>{ changetitle(e.target.value) }}></input>
      </div>

      <div>
      내용 ::  <input onChange={(e)=>{ changePost(e.target.value) }}></input>
      </div>

      <form onSubmit={addPostList}>
        <button>글 생성</button>
      </form>



    </div>
  );

  function addPostList(e) 
  {
    e.preventDefault();
    var day = new Date();
    dummy.PostTable.Day = day.date.toLocaleString('en-us');
    console.log( dummy.PostTable );
  }

  function changetitle(e)
  { dummy.PostTable.Title = e; }

  function changePost(e)
  { dummy.PostTable.Post = e; }
}

export default FirstPage;
