import { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import DB from "../Data/DataBase";
import "../GUI/GuiStyle.css";

function ForumPage() 
{
    var hook = useLocation().pathname.substring(7);
    if(useLocation().pathname == 0) { hook = 0; }
    var [page,page_] = useState(hook);
    var [updata,updata_] = useState(0);
    var look = [];
    
    if(DB.PostList.length == 0 ) { return(<div>게시글이 없습니다.</div>); } 

    for(var i= page*5; i < page+5; i++)
    {
        if(i >= DB.PostList.length) { break; }
        look.push(i);
    }

    function updataFuc()
    { 
        if(updata < 10) { updata_(updata +1); } else {updata_(0);} 
    }

    function postDelete(num,e)
    {
        e.preventDefault();
        DB.PostList.splice(num,1);
        updataFuc(); 
    }

    const setLook = look.map((item,index) => 
        <div key={index}>
            <button onClick={(e)=> {postDelete(item,e)}}>삭제</button>
            <Link to = {`/${DB.PageList[2][0]}/${page}/${item}` }> 
                :: {`${DB.PostList[item].Day}`}
                :: {`${DB.PostList[item].Title}`}
            </Link>
        </div>
    );

    
    return(
        <div>
            {setLook}
            
            <div>
            <button className="forumInput">이전</button>
            
            <textarea className="forumInput" placeholder={hook} />

            <button className="forumInput">이후</button>
            </div>
        </div>
    );
}

export default ForumPage;
