import { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import DB  from "../Data/DataBase.json"
import * as Func from '../Function';


function PostPage() 
{
    var adrres = Func.StringSub("/",useLocation().pathname);
    var [look,look_] = useState(adrres[3]);

    console.log(look);

    return(
        <div>
            <div>제목::{DB.PostList[look].Title}</div>
            <div>내용::{DB.PostList[look].Title}</div>
            <div>
                <Link to={`/${DB.PageList[0][0]}/${adrres[2]}`}>
                    <button>돌아가기</button>
                </Link>

                <Link to={`/${adrres[1]}/${adrres[2]}/${adrres+1}`}>
                    <button>다음 글</button>
                </Link>

       
            </div>
        </div>
    );  
}

export default PostPage;
