import { useState } from "react";
import DB from "../Data/DataBase";

function PostingPage() 
{
    console.log("reset");
    var check = [false,false];
    var [tilte,tilte_] = useState("");
    var [post,post_] = useState("");

    function changer(type,string_,e)
    {
        switch(type)
        {
            case 0: { tilte_(string_); }break;
            case 1: { post_(string_); }break;
        }
    }

    function result(e)
    {
        e.preventDefault();
        let timer = new Date();
        DB.PostList.push(
            { 
                Code: 0, Title: tilte, Post: post, 
                Day: `${timer.getFullYear()}-${timer.getMonth()+1}-${timer.getDay()}`
            }
        );
        console.log(DB.PostList);
    }
    
    return(
        <div>
            <div>
                제목:: <input onChange={(e)=> {changer(0,e.target.value,e)}} />
            </div>

            <div>
                내용:: <input onChange={(e)=> {changer(1,e.target.value,e)}} />
                <button onClick={result}>완료</button>
            </div>
        </div>
    );
}

export default PostingPage;
