import { useState } from "react";
import { useLocation } from "react-router-dom";
import DB  from "../Data/DataBase.json"
import * as Func from '../Function';


function PostPage() 
{
    var [look,look_] = useState(useLocation().pathname.substring(8));
    
    
    return(
        <div>
            <div>제목::{DB.PostList[look].Title}</div>
            <div>내용::{DB.PostList[look].Title}</div>
        </div>
    );  
}

export default PostPage;
