import { Link, Route  } from "react-router-dom";
import dummy from "../Data/Dummy.json"

function MainPage() 
{
  if(dummy.PostList.length != 0)
  {
    console.log(dummy.PostList)

    return (
      <div>
        <table>
          <tbody>
            {dummy.PostList.map( data => (
              <tr>
                <td><button onClick={(e)=>{deletePostPage(data.Number,e);}}>삭제</button></td>
                <td><Link to={"/Post"}> <button>내용</button></Link>/////</td>
                <td>{data.Day} /////</td>
                <td>{data.Title}</td>
              </tr>
            ))}
          </tbody>
        </table>  
      </div>
    );
  }
  else { return ( <div> 게시글이 없습니다.</div> ); }

  function deletePostPage(num,e)
  {
    for(let i = 0; i < dummy.PostList.length; i++)
    { 
      if(i == num) 
      { 
        console.log(num + "+" + i);
        dummy.PostList.pop(i);
        console.log(dummy.PostList)
      }
    }
  }
}

export default MainPage;
