import { useState } from "react";
import { Link, useLocation, useHistory } from "react-router-dom";
import DB  from "../Data/DataBase.json"
import * as Func from '../Function';


function PostPage() 
{
    var adrres = Func.StringSub("/",useLocation().pathname);
    adrres[3] = Number(adrres[3]);
    
    //여기서 제한주면되긴함 ㅋㅋ 

    console.log(adrres);

    function AS()
    {
        console.log("hellow");
        //<Link to={`/${DB.PageList[0][0]}/${adrres[2]}`}></Link>

    }

    /*
         <Link to={`/${adrres[1]}/${adrres[2]}/${adrres[3]+1}`}>
                    <button>다음 글</button>
                </Link>
    */

    return(
        <div>
            <div>제목::{DB.PostList[adrres[3]].Title}</div>
            <div>내용::{DB.PostList[adrres[3]].Post}</div>
            <div>
                <Link to={`/${DB.PageList[0][0]}/${adrres[2]}`}>
                    <button>돌아가기</button>
                </Link>


                <Link to={`/${adrres[1]}/${adrres[2]}/${adrres[3]-1}`}>
                    <button>이전 글</button>
                </Link>

                <button onClick={AS}>다음 글</button>
                

            </div>
        </div>
    );  
}

export default PostPage;
