import dummy from "../Data/Dummy.json"

function FirstPage() 
{ 
  dummy.PostTable.Title = dummy.PostTable.Day = dummy.PostTable.Post = "";

  return (
    <div className="Appa">  
      <Link to={`/${setPage[1]}`}>
      <div>  제목 ::
        <input onChange={(e)=>{ changetitle(e.target.value) }}></input>
      </div>

      <div>  내용 ::
        <input onChange={(e)=>{ changePost(e.target.value) }}></input>
      </div>

      <form onSubmit={addPostList}> 
        <button>글 생성</button>
      </form>
      </Link>
    </div>
  );

  function addPostList(e) 
  {
    e.preventDefault();
    var date = new Date();
    dummy.PostTable.Day = `${date.getFullYear()}-${date.getMonth()+1}-${date.getDate()}`;
    dummy.PostList.push
    ( 
      { "Title": dummy.PostTable.Title, 
        "Day" : dummy.PostTable.Day, 
        "Post" : dummy.PostTable.Post 
      }
    );
  }

  function changetitle(e)
  { dummy.PostTable.Title = e; }

  function changePost(e)
  { dummy.PostTable.Post = e; }
}

export default FirstPage;
