import { useState } from "react";
import { Link } from "react-router-dom";
import DB from "../Data/DataBase";

function ForumPage() 
{
    var [page,page_] = useState(0);
    var [updata,updata_] = useState(0);
    var look = []; // 랜더링할 번호 
    
    console.log("reset");
    if(DB.PostList.length == 0 ) { return(<div>게시글이 없습니다.</div>); } 

    for(var i= page*5; i < page+5; i++) // 0번부터 5번까지 
    {
        if(i >= DB.PostList.length) { break; } // 5개가 없을경우 멈춤 
        look.push(i); // 해당 번호를 입력 
    }

    function updataFuc()
    { 
        if(updata < 10) { updata_(updata +1); } else {updata_(0);} 
    }

    function postDelete(num,e)
    {
        e.preventDefault();

        DB.PostList.slice(num,1);
        
        console.log(DB.PostList);
        updataFuc(); //업데이트 
    }

    const nameList = look.map((item,index) => 
        <div key={index}>
            <button onClick={(e)=> {postDelete(item,e)}}>삭제</button>
            <Link to = {`/${DB.PageList[2][0]}`}> 
                <button>확인</button>
            </Link>
            :: {`${DB.PostList[item].Day}`}
            :: {`${DB.PostList[item].Title}`}
        </div>
    );

    // loock의 숫자만큼 랜더링시킨다.
    return(
        <div>
            {nameList}

        </div>
    );
}

export default ForumPage;
