import DB  from "../Data/DataBase.json"
import { BrowserRouter, Link } from 'react-router-dom';
import { useLocation } from "react-router-dom";
import * as Func from '../Function';
import "./GuiStyle.css"

function TopMenu() 
{
    var page =  Func.StringSub("/",useLocation().pathname);

    // 맵을써서 일정 부분만 검은색으로? 

    const setLook = DB.PageList.map((item,index) => 
    <div key={index}>
        <button onClick={(e)=> {postDelete(item,e)}}>삭제</button>
        <Link to = {`/${DB.PageList[2][0]}/${page}/${item}` }> 
            <button>확인</button>
        </Link>
        :: {`${DB.PostList[item].Day}`}
        :: {`${DB.PostList[item].Title}`}
    </div>
    );

    return(
        <div>
            <div className="topMenu"> Blog </div>

            <div className="topButtonOut">

                <Link to = {`${DB.PageList[0][0]}/0`}>
                    <div className="topButton"> {`${DB.PageList[0][1]}`} </div>
                </Link>

                <Link to = {`${DB.PageList[1][0]}`}>
                    <div className="topButton"> {`${DB.PageList[1][1]}`} </div>
                </Link>

            </div>
        </div>
    );  
}

export default TopMenu;

